import React , { Component } from 'react'

class ServiceDetails extends Component {
    constructor(props) {
        super(props) 
         this.state={
            amt: 0,
            serPer: '0.1',
            cName: '',
            tipVal: 0,
            tipCal: 0,
            totalTip: 0,
            custCount: 0 ,
            listItems: [ {name : '', tip : 0 } ]
        }
        
    }
    ChangeAmt = (event) => {this.setState({amt: event.target.value})} ;
    ChangeSerPer = (event) => {this.setState({serPer: event.target.value})} ;
    ChangeCName = (event) => {this.setState({cName: event.target.value})} ;
    TipCal = (event) => {
                        this.setState({tipCal:  this.state.amt *  this.state.serPer,
                            listItems: [...this.state.listItems,{name : '', tip : 0 }],
                            custCount: this.state.custCount + 1,
                            totalTip: this.state.totalTip + this.state.amt *  this.state.serPer }) 
                        };

                        
    OnAdd = (event) => {event.prevent.default()};
    
    render () {
        const {amt , serPer , cName , tipCal , totalTip , custCount } = this.state
        return (
            <form>
            <div>
                Enter your bill amount Rs.
                <p>
                <input type="number" name="Amt" value={amt} onChange={this.ChangeAmt} />
                </p>
                <p>
                How was the service ?
                    <select name="serPer" id="SerPer" value={serPer} onChange={(event) => this.setState({serPer: event.target.value})} >
                        <option value="0.2">Excellent - 20%</option>
                        <option value="0.1">Moderate - 10%</option>
                        <option value="0.05">Bad - 5%</option>
                    </select>
        
                    <input type="text" placeholder="Customer Name" 
                    value={cName} 
                    onChange={this.ChangeCName}required />

                    <input type="button" value="Add Customer"   onClick = {() => { this.TipCal() }} />                     
                </p>
                <p>
                Tip Amount :Rs
                    <input type="number" value={tipCal} disabled/>
                </p>

                {this.state.listItems.map((item,index) => { 
                return (
                <div key={index}>
                    <div>{item.name} </div>
                    <div>{item.tip} </div>
                </div>
                )
            })}
                Customer List
                    Total number of customers:
                    <input type="number" value={custCount} disabled/>
                    Total tip Amount :Rs
                    <input type="number" value={totalTip} disabled/>

            </div>
            </form>
        )
    }
}
export default ServiceDetails;
