 import React , { Component } from 'react'
import InputForm from '../Components/InputForm'

class ArrayDisplay extends Component {
    constructor(props){
        super(props)
        
    }

    
    render(){
        
        var sysDate = new Date()
        var  toDay = sysDate.toDateString()
        console.log(sysDate,'System Date')
        console.log(toDay,'Todays Date')
        return(
            <div text-align='left'>
                <ul>
                    {this.props.Displaylt.map((item, index) => (
                    <li key={index}>
                        <div>
                        <input type="checkbox" id={index} name={index} checked={item.arrcheck} onClick = {()=>{this.props.updateMe(index)}} />
                        <div className={item.arrcheck? 'truechk': '' } >
                            {console.log(sysDate , item.arrdate , 'checking dates')}
                            <div className={this.state.sysDate > item.arrdate ? 'overDue'  : ''} >   
                                {`${item.arrtask}- ${item.arrtaskdesc} to be completed by ${item.arrdate }`}  
                            </div>
                        </div>
                        <input type="button" value="Delete" 
                        onClick = {()=>{this.props.deleteMe(index)}} />
                        </div>
                    </li>
                    ))}
                </ul>
            </div>
        
        )
        
                    
    }
}

export default ArrayDisplay
