import React , { Component } from 'react';
import BillDetails from './BillDetails'
import DataSummary from './DataSummary'
import CustomerList from './CustomerList'
class ServiceDt extends Component {
    constructor(props) {
        super(props) 
         this.state={
              serPer: 0, 
            cName: '',
            tipCal: 0,
            totalTip: 0,
            custCount: 0,
            listItems: []
               }
            }

            
            
    render () {
        
        
        const {serPer , cName , tipCal , totalTip , custCount } = this.state
        return(
            <>
        
            <div>
                
                How was the service ?
                <select name="serPer" id="SerPer" value={serPer} onChange={(event) => this.setState({serPer: event.target.value})} >
                    <option value="0">Please Select the option</option>
                    <option value="0.2">Excellent - 20%</option>
                    <option value="0.1">Moderate - 10%</option>
                    <option value="0.05">Bad - 5%</option>
                </select>
                <br />
                <br />
                Customer  Name :            
                <input type="text" placeholder="Customer Name" 
                value={cName} 
                onChange={(event) => {this.setState({cName: event.target.value ,
                    tipCal: this.props.bAmt *  this.state.serPer
                     })}} required />
    
            <p>
                Tip Amount :Rs
                <input type="number" value={this.state.tipCal} disabled/>
            </p>
            <input type="button" value="Add Customer" 
            onClick={(event) => 
            {this.setState({listItems: [...this.state.listItems,
                                        {name : this.state.cName, tip : this.state.tipCal }
                                    ],
                            custCount: this.state.custCount + 1,
                            totalTip: this.state.totalTip + this.state.tipCal})
                            }  } />

        <div>
            Customer List
            <ul>
                {this.props.Displaylt.map((item, index) => (
<li key={index}>{`${item.name} offered a tip of Rs. ${item.tip }`}</li>
                        ))}
            </ul>
</div>
   

   
        
            <DataSummary summCount = {this.state.custCount}
                        summTip = {this.state.totalTip} />

                    {console.log(this.state.cName)}
                    {console.log(this.state.tipCal)}
                     {console.log(this.state.listItems)}
                     {console.log(this.state.custCount)}
                     {console.log(this.state.totalTip)}
            
            </div>
        </>
        )
    }
}

export default ServiceDt;
