import React , { Component } from 'react'
import InputForm from './InputForm'
//https://www.w3schools.com/howto/howto_js_display_checkbox_text.asp
//<del> to strick through
class ArrayDisplay extends Component {
    constructor(props){
        super(props)
    }

    render(){
        return(
            <div text-align='left'>
                <ul>
                    {this.props.Displaylt.map((item, index) => (
                    <li key={index}>
                        <div>
                        <input type="checkbox" id={index}  />
                        {`${item.arrtask}- ${item.arrtaskdesc} to be completed by ${item.arrdate }`}
                        <input type="button" value="Delete" onClick={this.props.deleteMe(index)}/>
                        </div>
                    </li>
                    ))}
                </ul>
            </div>
        )
    }
}

export default ArrayDisplay
