import React , { Component } from 'react'
import InputForm from '../Components/InputForm'

class ArrayDisplay extends Component {
    constructor(props){
        super(props)
    }
    checkBoxChecked (index) {
        if (index.checked == true) {
            console.log("checkbox is checked")
        }
        else {
            console.log("checkbox is unchecked")
        }
    }
    
    render(){

        return(
            <div text-align='left'>
                <ul>
                    {this.props.Displaylt.map((item, index) => (
                    <li key={index}>
                        <div>
                        <input type="checkbox" id={index} onClick ={this.checkBoxChecked(index)} />
                        {`${item.arrtask}- ${item.arrtaskdesc} to be completed by ${item.arrdate }`} 
                        
                        <input type="button" value="Delete" 
                        onClick = {()=>{this.props.deleteMe(index)}} />
                        {console.log(item.arrdate)}
                        </div>
                    </li>
                    ))}
                </ul>
            </div>
        )
    }
}

export default ArrayDisplay
