import React , { Component } from 'react';
import BillDetails from './BillDetails'


class ServiceDt extends Component {
    constructor(props) {
        super(props) 
         this.state={
            serPer: 0, 
            cName: '',
            tipCal: 0,
            totalTip: 0,
            custCount: 0
               }
            }

    render () {
        const {serPer , cName , tipCal , totalTip , custCount } = this.state
        return(
            <>
        
            <div>
                
                How was the service ?
                <select name="serPer" id="SerPer" value={serPer} onChange={(event) => this.setState({serPer: event.target.value})} >
                    <option value="0">Please Select the option</option>
                    <option value="0.2">Excellent - 20%</option>
                    <option value="0.1">Moderate - 10%</option>
                    <option value="0.05">Bad - 5%</option>
                </select>
                <br />
                <br />
                Customer  Name :            
                <input type="text" placeholder="Customer Name" 
                value={cName} 
                onChange={(event) => {this.setState({cName: event.target.value , 
                    tipCal: this.props.bAmt *  this.state.serPer})}} required />
    
            <p>
                Tip Amount :Rs
                <input type="number" value={this.state.tipCal} disabled/>
            </p>
            </div>
        </>
        )
    }
}

export default ServiceDt;
