import React , { Component } from 'react';
import ServiceDt from './ServiceDt';

class BillDetails extends Component {
    constructor(props) {
        super(props) 
         this.state={
           amt: 0 }
        }
        ChangeAmt = (event) => {this.setState({amt: event.target.value})} ;    
    render() {
        const {amt } = this.state
        return (
            
            <div>
                
                Enter your bill amount : Rs.
            
                <input type="number" name="Amt" value={amt} onChange={this.ChangeAmt} autoFocus/> 
                <br />
                <br />
            
            <ServiceDt bAmt = {this.state.amt}/>
            </div>
        )
    }
}

export default BillDetails
