import React , { Component } from 'react'
import InputForm from '../Components/InputForm'

class ArrayDisplay extends Component {
    constructor(props){
        super(props)
    }
    
    handleCheckBox = (item) => {
        console.log('in Checkbox <DEL>')
        this.setState({taskList:[...this.state.taskList.slice(0,item),
            ...this.state.taskList.slice(item+1)]})
    }

    render(){
        
        return(
            <div text-align='left'>
                <ul>
                    {this.props.Displaylt.map((item, index) => (
                    <li key={index}>
                        <div>
                        <input type="checkbox" id={index} onclick={this.handleCheckBox}  />
                        {`${item.arrtask}- ${item.arrtaskdesc} to be completed by ${item.arrdate }`}
                        <input type="button" value="Delete" 
                        onClick = {()=>{this.props.deleteMe(index)}} />
                        {console.log(item.arrdate)}
                        </div>
                    </li>
                    ))}
                </ul>
            </div>
        )
    }
}

export default ArrayDisplay
