import React , { Component } from 'react';
import BillDetails from './BillDetails'

class ServiceDt extends Component {
    constructor(props) {
        super(props) 
         this.state={
            serPer: 0, 
            cName: ''
               }
            }
    render () {
        return(
            <p>
                How was the service ?
                <select name="serPer" id="SerPer" value={serPer} onChange={(event) => this.setState({serPer: event.target.value})} >
                    <option value="0.2">Excellent - 20%</option>
                    <option value="0.1">Moderate - 10%</option>
                    <option value="0.05">Bad - 5%</option>
                </select>
    
                <input type="text" placeholder="Customer Name" 
                value={cName} 
                onChange={(event) => {this.setState({cName: event.target.value})}} required />
    
                <input type="button" value="Add Customer"   
                onClick = {(event) => { this.setState({tipCal:  this.state.amt *  this.state.serPer})}} />        
            </p>


        )
    }
}

export default ServiceDt;
