import React , { Component } from 'react'
import InputForm from '../Components/InputForm'

class ArrayDisplay extends Component {
    constructor(props){
        super(props)
        this.state={
            isCheck: false
        }
    }
    chkdt = (index) =>{
        console.log(index,'inside chk box')
        this.setState({
            Displaylt: this.props.Displaylt.map((newArr, ind) =>  {
                if (index == ind)
                {
                    return{ ...newArr,arrcheck: !newArr.arrcheck}
                }
                return newArr
        })
     })
    }
        
    render(){
    
    const{isCheck} = this.state
        return(
            <div text-align='left'>
                <ul>
                    {this.props.Displaylt.map((item, index) => (
                    <li key={index}>
                        <div>
                        
                        <input type="checkbox" id={index} name={index} checked={isCheck} onClick={()=>{this.chkdt(index)}} />
                        <span className={this.state.isCheck? 'truechk': '' } >
                        {`${item.arrtask}- ${item.arrtaskdesc} to be completed by ${item.arrdate }`}  
                        </span>
                        <input type="button" value="Delete" 
                        onClick = {()=>{this.props.deleteMe(index)}} />
        
                        
                        </div>
                    </li>
                    ))}
                </ul>
            </div>
        
        )
        
                    
    }
}

export default ArrayDisplay
