import React , { Component } from 'react'
import InputForm from '../Components/InputForm'

class ArrayDisplay extends Component {
    constructor(props){
        super(props)
        this.state={
            isCheck: false
        }
    }
chkdt = (arrdt) =>{
    console.log(arrdt)
}
    
    render(){
    
    const{isCheck} = this.state
        return(
            <div text-align='left'>
                <ul>
                    {this.props.Displaylt.map((item, index) => (
                    <li key={index}>
                        <div>
                        {this.chkdt(item.arrdate)}
                        <input type="checkbox" id={index} name={index} checked={isCheck? true:false} />
                        {`${item.arrtask}- ${item.arrtaskdesc} to be completed by ${item.arrdate }`} 
                        <input type="button" value="Delete" 
                        onClick = {()=>{this.props.deleteMe(index)}} />
                        
                        </div>
                    </li>
                    ))}
                </ul>
            </div>
        
        )
        
                    
    }
}

export default ArrayDisplay
