import React, { Component } from 'react'

class BillDetails extends Component {

    constructor(props) {
        super(props) 
         this.state={
            amt: 0
        }
    }

            render () {
                const {amt } = this.state
        return (
            
                <div>
                    Enter your bill amount Rs.
                    <p>
                        <input type="number" name="Amt" value={amt} onChange={this.ChangeAmt} />
                    </p>
                </div>
                )
            }
}
        
export default BillDetails ;
