export default class ChildB extends Component {
    constructor(props) {
        super(props) 
        this.state={
            a: this.props.val
        }
    }
    handleChange=(event)=>{
        this.setState({a: event.target.value})
    }

    handleAdd=(event)=>{
        this.props.changeName(this.state.a)
    }
    

    render() {
        const {a}=this.state
        return(
            <div> 
                <input type= "text"  value={this.props.val} onChange = {this.handleChange}/>
                <input type="button"  value="Change Name" onClick = {this.handleAdd}/>
            </div>

        )
    }


}
