import React , { Component } from 'react'
import InputForm from '../Components/InputForm'

class ArrayDisplay extends Component {
    constructor(props){
        super(props)
        this.state={
            isCheck: false
        }
    }

    
    render(){
    
        
            var onChecked = () => item.update('checked', state => !state);
            var style = {
              textDecoration: item.get('checked') ? 'line-through' : 'none',
            };
            

        return(
            <div text-align='left'>
                <ul>
                    {this.props.Displaylt.map((item, index) => (
                    <li key={index}>
                        <div>
                        <label style={style}>
                            <input type="checkbox"
                            onChange={onChecked}
                            checked={item.get('checked')} />
                            {item.get('text')}
                        </label>          
                        {`${item.arrtask}- ${item.arrtaskdesc} to be completed by ${item.arrdate }`} 
                        
                        <input type="button" value="Delete" 
                        onClick = {()=>{this.props.deleteMe(index)}} />
                        {console.log(item.arrdate)}
                        </div>
                    </li>
                    ))}
                </ul>
            </div>
        
        )
        
                    
    }
}

export default ArrayDisplay
