import React , { Component } from 'react'

class ServiceDetails extends Component {
    constructor(props) {
        super(props) 
         this.state={
            amt: 0,
            serPer: '0.1',
            cName: '',
            tipVal: 0,
            tipCal: 0,
            custDetails: '',
            totalTip: 0,
            custCount: 0 ,
            listItems: [ {name : '', perc : 0.1 } ]
        }
        
    }
    ChangeAmt = (event) => {this.setState({amt: event.target.value})} ;
    ChangeSerPer = (event) => {this.setState({serPer: event.target.value})} ;
    ChangeCName = (event) => {this.setState({cName: event.target.value})} ;
    TipCal = (event) => {
                        this.setState({tipCal:  this.state.amt *  this.state.serPer	}) 
                        this.setState({custDetails: this.state.custDetails +','+ this.state.cName})
                        this.setState({custCount: this.state.custCount + 1})
                        this.setState({totalTip: this.state.totalTip + this.state.amt *  this.state.serPer	})

                        };

                        

    OnAdd = (event) => {event.prevent.default()};
    

    render () {
        const {amt , serPer , cName , tipCal , custDetails , totalTip , custCount } = this.state
        return (
            <form>
            <div>
                Enter your bill amount Rs.
                <p>
                <input type="number" name="Amt" value={amt} onChange={this.ChangeAmt} />
        
                </p>
                {this.state.listItems.map((item) => { 
                return (
                <p>
                    How was the service ?
                    <select name="SerPer" id="SerPer" value={item.perc} onChange={(event) => this.setState({serPer: event.target.value})} >
                        <option value="0.2">Excellent - 20%</option>
                        <option value="0.1">Moderate - 10%</option>
                        <option value="0.05">Bad - 5%</option>
                    </select>
        
                    <input type="text" placeholder="Customer Name" 
                    name ="CName" 
                    value={item.name} 
                    onChange={this.ChangeCName}required />
                    
                </p>
                )
            })}
                <input type="button" value="Add Customer"   onClick = { () => {
                    this.setState({listItems: [...this.state.listItems,{name : '', perc : 0.1 }]})
                }} />
                
                {/* {() => { this.TipCal() }} />                 */}
                <p>

                    Tip Amount :Rs
                    <input type="number" value={tipCal} disabled/>
                </p>


            

                Customer List
                <ul>
                    <li>{this.state.cName}  offered a tip of  {this.state.tipCal}  Rupees </li>
        
                </ul>
                    Total number of customers:
                    <input type="number" value={custCount} disabled/>
                    Total tip Amount :Rs
                    <input type="number" value={totalTip} disabled/>

            </div>
            </form>
        )
    }
}
export default ServiceDetails;
