import React , { Component } from 'react'
import InputForm from './InputForm'

class ArrayDisplay extends Component {
    constructor(props){
        super(props)
    }
    render(){
        return(
            // <div>
            //     My To Do List
            //         {this.props.Displaylt.map((item, index) => (
            //         <input type='checkbox' key={index}><strong>{`${item.arrtask}- ${item.arrtaskdesc} to be completed by ${item.arrdate }`}</strong> </input>
            //             ))}
            <div>
                My To Do List
                {this.props.Displaylt.map((item,index) =>(
                <p key={index}>
                    <input type="checkbox" name={index} id={index} value = {`${item.arrtask}- ${item.arrtaskdesc} to be completed by ${item.arrdate }`} ></input>
                    
                </p>
                ))}
            </div>
        )
    }
}

export default ArrayDisplay
