import React , { Component } from 'react'
import InputForm from '../Components/InputForm'

class ArrayDisplay extends Component {
    constructor(props){
        super(props)
        this.state = {
            isChecked: false
        }
    }
    onCheckBoxClick = (event) => {
        if (index.checked == true){
            <del>{`${item.arrtask}- ${item.arrtaskdesc} to be completed by ${item.arrdate }`}</del>
        } else {
            {`${item.arrtask}- ${item.arrtaskdesc} to be completed by ${item.arrdate }`}
        }
    }

    render(){
        const{isChecked} = this.state
        return(
            <div text-align='left'>
                <ul>
                    {this.props.Displaylt.map((item, index) => (
                    <li key={index}>
                        <div>
                        <input type="checkbox" id={index} checked={isChecked}  onChange={this.onCheckBoxClick}
                         />
                        <input type="button" value="Delete" 
                        onClick = {()=>{this.props.deleteMe(index)}} />
                        {console.log(item.arrdate)}
                        </div>
                    </li>
                    ))}
                </ul>
            </div>
        )
    }
}

export default ArrayDisplay
