        } else {
            
            console.log("Checkbox is unchecked")
        }
    }

    render(){
        const{isChecked} = this.state
        return(
            <div text-align='left'>
                <ul>
                    {this.props.Displaylt.map((item, index) => (
                    <li key={index}>
                        <div>
                        <input type="checkbox" id={index} checked={isChecked} onChange={this.onCheckBoxClick(index)}/>
